package com.myCompany;/*      Input: 123        Output: 321        Input: -123        Output: -321        Input: 120        Output: 21*/public class ReverseInteger {    public static void main(String[] args) {        int reverse = new ReverseInteger().reverse(123);        System.out.println(reverse);    }    public int reverse(int x) {       int firstNum = x % 10;       int secNum = x % 100 / 10;       int thirdNum = x / 100;        return firstNum * 100 + secNum * 10 + thirdNum;    }}